mapper.insert:
insert into ice_member(im_id, im_name, im_password, im_email_id, im_email_domain, im_basic_addr, im_detail_addr)
       values (#{im_id}
       		  ,#{im_name}
       		  ,#{im_password}
       		  ,#{im_email_id}
       		  ,#{im_email_domain}
       		  ,#{im_basic_addr}
       		  ,#{im_detail_addr}
       		  )

mapper.select:
select im_id, im_name, im_password, im_email_id, im_email_domain, im_basic_addr, im_detail_addr, to_char(im_reg_date, 'yyyy-mm-dd') as im_reg_date
  from ice_member
 where im_id = #{im_id}

mapper.update:
update ice_member
im_id = #{im_id}, im_name = #{im_name}, im_password = #{im_password}, im_email_id = #{im_email_id}, im_email_domain = #{im_email_domain}, im_basic_addr = #{im_basic_addr}, im_detail_addr = #{im_detail_addr},
where id = #{im_id}
