mapper.insert:
insert into ice_member(id, name, password, email_id, email_domain, basic_addr, detail_addr)
       values (#{id}
       		  ,#{name}
       		  ,#{password}
       		  ,#{email_id}
       		  ,#{email_domain}
       		  ,#{basic_addr}
       		  ,#{detail_addr}
       		  )

mapper.select:
select id, name, password, email_id, email_domain, basic_addr, detail_addr, to_char(reg_date, 'yyyy-mm-dd') as reg_date
  from ice_member
 where id = #{id}
